mapper.select:
select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

select s.*,c.name categoryName from setmeal s left join category c on s.category_id = c.id
and s.name like concat('%',#{setmealPageQueryDTO.name},'%')
and s.status = #{setmealPageQueryDTO.status}
and s.category_id = #{setmealPageQueryDTO.categoryId}
order by s.create_time desc

mapper.update:
update setmeal
category_id=#{categoryId}, `name`=#{name}, price=#{price}, status=#{status}, description=#{description}, image=#{image}, create_time=#{createTime}, update_time=#{updateTime}, create_user=#{createUser}, update_user=#{updateUser}
where id = #{id}
